#!/bin/bash
# Extract Claude OAuth Token for CloudMax Subscription
# Usage: ./scripts/get-claude-oauth-token.sh

set -e

CREDENTIALS_FILE="$HOME/.claude/.credentials.json"

if [ ! -f "$CREDENTIALS_FILE" ]; then
  echo "❌ Credentials file not found at: $CREDENTIALS_FILE"
  echo ""
  echo "Please authenticate first:"
  echo "  claude auth login"
  exit 1
fi

# Check if jq is available
if ! command -v jq &> /dev/null; then
  echo "⚠️  jq not found. Installing jq or extracting token manually..."
  echo ""
  echo "Token location: $CREDENTIALS_FILE"
  echo "Look for: claudeAiOauth.accessToken"
  echo ""
  echo "Or install jq:"
  echo "  brew install jq  # macOS"
  echo "  apt-get install jq  # Linux"
  exit 1
fi

# Extract token
TOKEN=$(jq -r '.claudeAiOauth.accessToken // empty' "$CREDENTIALS_FILE")

if [ -z "$TOKEN" ] || [ "$TOKEN" == "null" ]; then
  echo "❌ OAuth token not found in credentials file"
  echo ""
  echo "Please authenticate with:"
  echo "  claude auth login"
  exit 1
fi

# Check token format
if [[ ! "$TOKEN" =~ ^sk-ant-oat ]]; then
  echo "⚠️  Warning: Token doesn't start with 'sk-ant-oat'"
  echo "   This might not be an OAuth token"
fi

echo "✅ OAuth Token Found!"
echo ""
echo "Token (starts with): ${TOKEN:0:20}..."
echo ""
echo "📋 Copy this token to GitHub Secrets:"
echo "   1. Go to: Settings → Secrets and variables → Actions"
echo "   2. Click: New repository secret"
echo "   3. Name: CLAUDE_CODE_OAUTH_TOKEN"
echo "   4. Value: (paste token below)"
